package sec02.exam10;

public class AssignmentOperatorExaple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		// A = A + B // A += B
		result += 10;
		System.out.println("result=" + result);
		result -= 5;
		System.out.println("result=" + result);
		result += 3;
		System.out.println("result=" + result);
		result /= 5;
		System.out.println("result=" + result);
		result %= 3;
		System.out.println("result=" + result);

	}

}
