package sec02.exam02;

public class FieldInitValueExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		FieldInitValue myFieldInitValue = new FieldInitValue();
		
		System.out.println("byteField: " + myFieldInitValue.byteField);
		System.out.println("shortField: " + myFieldInitValue.shortField);
		System.out.println("intField: " + myFieldInitValue.intField);
		System.out.println("longField: " + myFieldInitValue.longField);
		System.out.println("booleanField: " + myFieldInitValue.booleanField);
		System.out.println("charField: " + myFieldInitValue.charField);
		System.out.println("floatField: " + myFieldInitValue.floatField);
		System.out.println("doubleField: " + myFieldInitValue.doubleField);
		System.out.println("arrField: " + myFieldInitValue.arrField);
		System.out.println("referenceField: " + myFieldInitValue.referenceField);

	}

}
